import { Link } from 'react-router-dom';
import Style from './Card.module.css' ;
import { connect } from 'react-redux';
import { addFavorite,removeFavorite } from '../../redux/actions';
import { useState } from 'react';


 function Card({ id, name, species, image, gender, onClose, addFavorite, removeFavorite,}) {

   const [isFav,setIsFav] = useState(false)

   const handleFavorite = () => {
      if (isFav) {
         setIsFav(false);
         removeFavorite(id);
      }else{
         setIsFav(true)
         addFavorite(
            id,
            species, image, gender, addFavorite, removeFavorite
         )
      }
   }

   return (
    
      <div className= {Style.car} >
        
         <div className={Style.divBot}>
    {     
      isFav ? (
          <button onClick={handleFavorite}>‚ù§Ô∏è</button>
      ) : (
          <button onClick={handleFavorite}>ü§ç</button>
      )
}
         <button className= {Style.boton} onClick={onClose}>X</button>
         </div>
      <Link to={`/detail/${id}`}>
           <h2 className= {Style.h2Na} >{name}</h2>
      
         <h2 className= {Style.h2Spe} >{species}</h2>
         <h2 className= {Style.h2Gen}   >{gender}</h2>
         <img  src={image} alt="imagen fallida" /> 
      </Link>
      </div>
  
   );
}

const mapDispatchToProps = (dispatch) => {
   return{
      addFavorite: (character) => {
         dispatch(addFavorite(character));
      },
      removeFavorite: (id) => {
         dispatch(removeFavorite(id))
      }
   }
}

export default  connect(null, mapDispatchToProps)(Card);
